// define a JS object called 'theme' that holds my theme data

const theme = {
  pageColor: '#d4fbff',
  titleColor: 'purple',
  textColor: '#141b33',
  font: 'sans-serif',
  size: {
    title: '50pt',
    subtitle: '30pt',
    text: '18pt',
  }
}

export default theme
